package javaprogrming;

public class DataTypesDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Numeric data Types
		
		int a=100, b=200;
		
		System.out.println("the value of a is:"+a);
		System.out.println("the value of b is:"+b);
		System.out.println(a+b);                   // Addition
		System.out.println("the value of a and b is:"+(a+b));
			
		byte by=100;
		System.out.println(by);
		
		short sh=3536;
		System.out.println(sh);
		
		long ln=1234567890123456L; // literal is needed at the end of value L or l
		System.out.println(ln);
		
		// number with decimal -- float, double
		
		float fl=123.1f; // literal is needed at the end of value F or f
		System.out.println(fl);
		
		double dbl=1365.48789545632;
		System.out.println(dbl);
		
		char C='P'; // single letter 
		System.out.println(C);
		
		String str="Poonam"; // multiple letter
		System.out.println(str);
		
		boolean bl=true; // allows only true and false 
		System.out.println(bl);
		
		boolean bl1= false;
		System.out.println(bl1);
		
		// char ch='ABC'; 			// invalid
		// String ch='ABC'; 		// Invalid
		//String ch = 'A'; 			// invalid
		//String ch= "A"; 			// Valid
		// boolean bl="true" 		// invalid
		//boolean bl1= "false"; 	// invalid
		//String bl=true;			// invalid 
		// String bl2= "true"; 		// Valid
		
		
		
		
		
		
		
	}

}
